import React, { Component } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import ApllicantInfo from "./applicant-info/applicant-info"
import "./application.scss"
import SupouseInfo from "./spouse-info/spouse-info"
import SpouseBasic from "./spouse-info/spouse-basic/spouse-basic"
import ScrollableTabsButtonAuto from "./spouse-info/spouse-tabs/spouse-tabs"
import ChildrenInfo from "./children-info/children-info"
import AddressContact from "./address-contact/address-contact"
import { inject, observer } from "mobx-react";
import { withStyles } from '@material-ui/core/styles';
const useStyles = makeStyles(theme => ({
    root: {
        width: '70%',
    },
    button: {
        marginTop: theme.spacing(1),
        marginRight: theme.spacing(1),
    },
    actionsContainer: {
        marginBottom: theme.spacing(2)
    },
    resetContainer: {
        padding: theme.spacing(3),
    },
}));

function getSteps() {
    return ['Applicant Info', 'Spouse Info', 'Children Info', 'Address & Contact'];
}


@inject('registrationStore')
@observer
class VerticalLinearStepper extends Component {
    //steps = getSteps();

    state = {
        activeStep: 0
    };
    handleNext = () => {
        // if (this.state.activeStep === 0) {
        //     if (this.props.registrationStore.applicationData.applicantInfo.childrenNumber > 0) {
        //         Array.from(new Array(parseInt(this.props.registrationStore.applicationData.applicantInfo.childrenNumber,10)), (v, i) => {
        //             this.props.registrationStore.applicationData.childrenInfo.push({})
        //             this.childrenTabs.push({ title: `Child ${i + 1}`, cmp: <ChildrenInfo handleDataChange={(attr, val, index) => this.handleChildrenDataChange(attr, val, index)} props={this.props} index={i} /> })
        //         });
        //         this.steps.push({
        //             title: 'Children Info', cmp: <ScrollableTabsButtonAuto
        //                 tabs={this.childrenTabs}
        //                 handleDataChange={(attr, val) => this.handleSpouseDataChange(attr, val)} props={this.props} />
        //         })

        //     }
        // }
        this.setState({ activeStep: this.state.activeStep + 1 });
    };
    handleBack = () => {
        this.setState({ activeStep: this.state.activeStep - 1 });
    };


    handleReset = () => {
        this.setState({ activeStep: 0 });
    };

    handleApplicantDataChange(attr, value) {
        this.props.registrationStore.applicationData.applicantInfo[attr] = value;
    }
    handleSpouseDataChange(attr, value) {
        this.props.registrationStore.applicationData.spouseInfo[attr] = value;
    }
    handleChildrenDataChange(attr, value, index) {
        this.props.registrationStore.applicationData.childrenInfo[index][attr] = value;
    }
    componentDidMount() {
        if (this.props.registrationStore.applicationData.childrenInfo.length > 0) {

        }
    }
    spouseTabs = {
        "Basic": {cmp: <SpouseBasic handleDataChange={(attr, val) => this.handleSpouseDataChange(attr, val)} props={this.props} index={0} /> },
       "Passport And Photo": {cmp: <SupouseInfo handleDataChange={(attr, val) => this.handleSpouseDataChange(attr, val)} props={this.props} index={1} /> }
    }
    childrenTabs = [
    ]
    steps = [
        { title: 'Applicant Info', cmp: <ApllicantInfo handleDataChange={(attr, val) => this.handleApplicantDataChange(attr, val)} props={this.props} /> },
        {
            title: 'Spouse Info', cmp: <ScrollableTabsButtonAuto
                tabs={this.spouseTabs}
                handleDataChange={(attr, val) => this.handleSpouseDataChange(attr, val)} props={this.props} />
        },
        { title: 'Address & Contact', cmp: <AddressContact handleDataChange={(attr, val) => this.handleSpouseDataChange(attr, val)} props={this.props} /> }
    ]
    getStepContent(step) {
        return this.steps[step]
    }
    render() {
        const { classes } = this.props;

        return (
            <div className="application-stepper-container">
                {/* {JSON.stringify(this.props.registrationStore.applicationData)} */}
                {/* {JSON.stringify(this.spouseTabs["Basic"])} */}
                {Object.entries(this.spouseTabs).forEach(([key, value]) => console.log(`${key}: ${value}`))}
                {/* {Object.entries(this.spouseTabs).forEach(([key, value]) =>  <div>{key}</div>)} */}

                <Stepper activeStep={this.state.activeStep} orientation="vertical">
                    {this.steps.map((step, index) => (
                        <Step key={step.title}>
                            <StepLabel>
                                {this.state.activeStep === 0 && 'Application Info'}
                            </StepLabel>
                            <StepContent>
                                {/* <div>{this.getStepContent(index).cmp}</div> */}
                                {this.state.activeStep === 0 && <ApllicantInfo handleDataChange={(attr, val) => this.handleApplicantDataChange(attr, val)} props={this.props} />}
                                {this.state.activeStep === 1 && <ScrollableTabsButtonAuto
                                    tabs={this.spouseTabs}
                                    handleDataChange={(attr, val) => this.handleSpouseDataChange(attr, val)} props={this.props} />}

                                    <div className="actions-container">
                                        <div>
                                            <Button
                                                disabled={this.state.activeStep === 0}
                                                onClick={this.handleBack}
                                                className={classes.button}
                                            >
                                                Back
                  </Button>
                                            <Button
                                                variant="contained"
                                                color="primary"
                                                onClick={this.handleNext}
                                                className={classes.button}
                                            >
                                                {this.state.activeStep === this.steps.length - 1 ? 'Finish' : 'Next'}
                                            </Button>
                                        </div>
                                    </div>
                            </StepContent>
                        </Step>
                    ))}
                </Stepper>
                {this.state.activeStep === this.steps.length && (
                    <Paper square elevation={0} className={classes.resetContainer}>
                        <Typography>All steps completed - you&apos;re finished</Typography>
                        <Button onClick={this.handleReset} className={classes.button}>
                            Reset
          </Button>
                    </Paper>
                )}
            </div>
        )
    };
}
export default withStyles(useStyles)(VerticalLinearStepper);
