                                <InputLabel id="year">Year</InputLabel>
                                <Select
                                    labelId="year"
                                    id="year"
                                    value={props && props.registrationStore.applicationData.applicantInfo.year}
                                    onChange={(event) => handleDataChange("year", event.target.value)}
                                >
                                    {Array.from(new Array(90), (v, i) =>
                                        <MenuItem value={currentYear - i}>{currentYear - i}</MenuItem>

                                    )}
                                </Select>
                            </FormControl>
                        </Grid>

                    </Grid>

                </div>
            </div>
        </React.Fragment>
    );
}
