import { observable, action, computed} from "mobx";
import ConfigStore from "./configStore"

const apis = {
    login(data){
      return fetch(ConfigStore.configData.domain + 'api/users/login/', {
        method: 'POST',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          "user": {
            "email": data.userName,
            "password": data.password
          }
        }),
      })
      .then((response) => response.json())
      .then((responseJson) => {
        if(responseJson.user){
          console.log(responseJson.user)

        }else{

        }        
        return responseJson;
      })
      .catch((error) => {
        console.error(error);
      });
    },
    logOut(data){
        return fetch(ConfigStore.configData.domain + 'api/users/signOut/', {
            method: 'POST',
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
              'Authorization': 'Token ' + data.token

            },
            body: JSON.stringify({
              "user": data
            }),
          })
          .then((response) => response.json())
          .then((responseJson) => {
            console.log(responseJson)
          })
          .catch((error) => {
            console.log(error);
          });
    }
}
class AuthStore {
    @observable authData=null;

    @action login (data) {
        return apis.login(data).then((data)=>this.setUserDataLocal(data));
    };
    @action getUserDataLocal () {
        this.authData = localStorage.getItem('authData');
    };
    @action setUserDataLocal (data) {
        localStorage.setItem('authData', JSON.stringify(data));
        this.authData = data;
        console.log(this.authData)
    };
    @action initLogin () {
        this.authData = JSON.parse(localStorage.getItem('authData'));
    };
    @action logOut () {
        return apis.logOut(this.authData.user).then(()=>{
            localStorage.setItem('authData',null);
            this.authData = null;
        });
    }
}
export default new AuthStore();
