import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import ApllicantInfo from "./applicant-info/applicant-info"
import "./application.scss"
import SupouseInfo from "./spouse-info/spouse-info"
import ScrollableTabsButtonAuto from "./spouse-info/spouse-tabs/spouse-tabs"
import ChildrenInfo from "./children-info/children-info"
import AddressContact from "./address-contact/address-contact"

const useStyles = makeStyles(theme => ({
    root: {
        width: '70%',
    },
    button: {
        marginTop: theme.spacing(1),
        marginRight: theme.spacing(1),
    },
    actionsContainer: {
        marginBottom: theme.spacing(2)
    },
    resetContainer: {
        padding: theme.spacing(3),
    },
}));

function getSteps() {
    return ['Applicant Info', 'Spouse Info', 'Children Info', 'Address & Contact'];
}

function getStepContent(step) {
    switch (step) {
        case 0:
            return <ApllicantInfo />;
        case 1:
            return <ScrollableTabsButtonAuto />;
        case 2:
            return <ChildrenInfo />;
        case 3:
            return <AddressContact />;
        default:
            return 'Unknown step';
    }
}
@inject('registrationStore')
@observer
class VerticalLinearStepper extends Component {
    steps  = getSteps();

    state={
        activeStep:0
    };
    handleNext = () => {
        this.setState({activeStep:prevActiveStep => prevActiveStep + 1});
    };
    handleBack = () => {
        this.setState({activeStep:prevActiveStep => prevActiveStep - 1});
    };

    handleReset = () => {
        setActiveStep(0);
    };
    render(){
        const { classes } = this.props;

    return (
        <div className="application-stepper-container">
            <Stepper activeStep={activeStep} orientation="vertical">
                {this.steps.map((label, index) => (
                    <Step key={label}>
                        <StepLabel>{label}</StepLabel>
                        <StepContent>
                            <div>{getStepContent(index)}</div>
                            <div className="actions-container">
                                <div>
                                    <Button
                                        disabled={activeStep === 0}
                                        onClick={handleBack}
                                        className={classes.button}
                                    >
                                        Back
                  </Button>
                                    <Button
                                        variant="contained"
                                        color="primary"
                                        onClick={handleNext}
                                        className={classes.button}
                                    >
                                        {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                                    </Button>
                                </div>
                            </div>
                        </StepContent>
                    </Step>
                ))}
            </Stepper>
            {activeStep === steps.length && (
                <Paper square elevation={0} className={classes.resetContainer}>
                    <Typography>All steps completed - you&apos;re finished</Typography>
                    <Button onClick={this.handleReset} className={classes.button}>
                        Reset
          </Button>
                </Paper>
            )}
        </div>
    )};
}
export default withStyles(useStyles)(Register);
