import React, {Component} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Paper from '@material-ui/core/Paper';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Link from '@material-ui/core/Link';
import Typography from '@material-ui/core/Typography';
import AddressForm from './addressForm/addressForm';
import PaymentForm from './paymentForm/paymentForm';
import Review from './review/review';
import CompleteRgeistration from './completeRgeistration/completeRgeistration';
import {inject, observer} from "mobx-react";
import registrationStore from "../stores/registrationStore"
import './registration.scss'



@inject('registrationStore')
@observer
class Register extends Component {
  classes = useStyles();
    state={
        activeStep:0
    };
  handleNext = () => {
    this.setState({activeStep: this.state.activeStep + 1});
  };

  handleBack = () => {
    this.setState({activeStep: this.state.activeStep -1});
    };
  render(){
  return (
      <div className="registration-container">
    <React.Fragment>
      <CssBaseline />
      <AppBar position="absolute" color="default" className="header">
        <Toolbar>
          <Typography variant="h6" color="inherit" noWrap>
            USA-DV   {this.props.registrationStore.registrationData.test}        
          </Typography>
        </Toolbar>
      </AppBar>
      <main className={this.classes.layout}>
        <Paper className={this.classes.paper}>
          <Typography component="h1" variant="h4" align="center">
          Registration
          </Typography>
          <Stepper activeStep={this.state.activeStep} className={this.classes.stepper}>
            {steps.map(label => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>
          <React.Fragment>
            {this.state.activeStep === steps.length ? (
              <React.Fragment>
                <Typography variant="h5" gutterBottom>
                  Thank you for your order.
                </Typography>
                <Typography variant="subtitle1">
                  Your order number is #2001539. We have emailed your order confirmation, and will
                  send you an update when your order has shipped.
                </Typography>
              </React.Fragment>
            ) : (
              <React.Fragment>
                {getStepContent(this.state.activeStep)}
                <div className={this.classes.buttons}>
                  {this.state.activeStep !== 0 && (
                    <Button onClick={this.handleBack} className={this.classes.button}>
                      Back
                    </Button>
                  )}
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={this.handleNext}
                    className={this.classes.button}
                  >
                    {this.state.activeStep === steps.length - 1 ? 'Place order' : 'Next'}
                  </Button>
                </div>
              </React.Fragment>
            )}
          </React.Fragment>
        </Paper>
        <Copyright />
      </main>
    </React.Fragment>
    </div>
  )};
}
export default Register;
