import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import SupouseInfo from "../spouse-info";
import ApllicantInfo from "../../applicant-info/applicant-info"
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <Typography
      component="div"
      role="tabpanel"
      hidden={value !== index}
      id={`scrollable-auto-tabpanel-${index}`}
      aria-labelledby={`scrollable-auto-tab-${index}`}
      {...other}
    >
      {value === index && <Box p={3}>{children}</Box>}
    </Typography>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `scrollable-auto-tab-${index}`,
    'aria-controls': `scrollable-auto-tabpanel-${index}`,
  };
}

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    width: '100%',
    backgroundColor: theme.palette.background.paper,
  },
}));

export default function ScrollableTabsButtonAuto({ handleDataChange, props, tabs }) {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      <AppBar position="static" color="default">
        <Tabs
          value={value}
          onChange={handleChange}
          indicatorColor="primary"
          textColor="primary"
          variant="scrollable"
          scrollButtons="auto"
          aria-label="scrollable auto tabs example"
        >
          {tabs.map((tab, i) => {
            return <Tab label={tab.title} {...a11yProps(i)} />
          })}
          {/* <Tab label="Basic" {...a11yProps(0)} />
          <Tab label="Passport and Photo" {...a11yProps(1)} /> */}
        </Tabs>
      </AppBar>
      {tabs.map((tab, j) => {
          {tab.cmp.map((cm,i)=>{
            return <TabPanel value={value} index={i}>
              {cm}
               </TabPanel>
          })}

      })}
      {/* <TabPanel value={value} index={0}>
        {tab1}

      </TabPanel>
      <TabPanel value={value} index={1}>
        {tab2}
      </TabPanel> */}
    </div>
  );
}
