import React, { useState } from 'react';
import Grid from '@material-ui/core/Grid';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import CountrySelect from '../../components/countrySelect'
import { TextField, withStyles, InputLabel, Select, MenuItem } from '@material-ui/core';
import "./children-info.scss"
import { DropzoneArea } from 'material-ui-dropzone'
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';

const useStyles = makeStyles(theme => ({
    formControl: {
        minWidth: 120,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
}));

function ChildrenInfo(props) {
    const classes = useStyles();
    const [day, setDay] = useState(null);
    const [month, setMonth] = useState(null);
    const [year, setYear] = useState(null);
    const [image, setImage] = useState(Array);
    const [passportStatus, setPassportStatus] = useState(null);
    const [country, setCountry] = useState(null);
    const [value, setValue] = React.useState('female');
    const [imagePath, setImagePath] = useState(null);

    const handleChange = event => {
        setValue(event.target.value);
    };
    function handleOnChange(value) {
        console.log(value)
    }
    const onCountryChange = (value) => {
        setCountry(value);
    }
    const setSelectedImage = (files) => {
        console.log(files)
        setImagePath(URL.createObjectURL(files[0]));

        setImage(files);
    }
    const currentYear = new Date().getFullYear() - 18;

    return (
        <React.Fragment >
            <div className="applicant-info-container spouse-info-container">
                <div className="addressForm">
                <Grid container spacing={5}>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                id="firstName"
                                name="firstName"
                                label="First name"
                                fullWidth
                                autoComplete="fname"
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                id="lastName"
                                name="lastName"
                                label="Last name"
                                fullWidth
                                autoComplete="lname"
                            />
                        </Grid>
                        <Grid container justify="flex-start" item xs={12} sm={6}>
                            <TextField
                                required
                                id="middleName"
                                name="middleName"
                                label="Middle Name"
                                fullWidth
                                autoComplete="middleName"
                            />
                            <FormControlLabel
                                control={<Checkbox color="primary" name="saveAddress" value="yes" />}
                                label="Don't have middle name"
                            />
                        </Grid>

                        <Grid item xs={12} sm={6}>
                            <CountrySelect placeHolder="Country Of Birth" onChange={onCountryChange} />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                id="cityOfBirth"
                                name="cityOfBirth"
                                label="City of Birth"
                                fullWidth
                                autoComplete="cityOfBirth"
                            />
                        </Grid>
                    </Grid>
                    {image.length == 0 && <Grid item  container  className="image-upload-container"
                        justify="flex-start" sm={2} >
                        <DropzoneArea showPreviewsInDropzone={true} filesLimit={1}
                            acceptedFiles={['image/jpeg']}

                            onChange={setSelectedImage}
                        />
                    </Grid>}




                </div>
            </div>
        </React.Fragment>
    );
}
export default (ChildrenInfo);
