import React, { useState } from 'react';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import ReactPhoneInput from 'react-phone-input-2';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import CountrySelect from '../../components/countrySelect'
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormLabel from '@material-ui/core/FormLabel';
import { TextField, withStyles, InputLabel, Select, MenuItem } from '@material-ui/core';
import "./applicant-info.scss"
const useStyles = makeStyles(theme => ({
    formControl: {
        minWidth: 120,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
}));

function ApllicantInfo(props) {
    const classes = useStyles();
    const [day, setDay] = useState(null);
    const [month, setMonth] = useState(null);
    const [year, setYear] = useState(null);
    const [country, setCountry] = useState(null);
    const [value, setValue] = React.useState('female');
    const handleChange = event => {
        setValue(event.target.value);
      };
    function handleOnChange(value) {
        console.log(value)
    }
    const onCountryChange = (value) => {
        setCountry(value);
    }
    const currentYear = new Date().getFullYear() - 18;

    return (
        <React.Fragment >
            <div className="addressForm">
                <Grid container spacing={5}>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            required
                            id="firstName"
                            name="firstName"
                            label="First name"
                            fullWidth
                            autoComplete="fname"
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            required
                            id="lastName"
                            name="lastName"
                            label="Last name"
                            fullWidth
                            autoComplete="lname"
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            required
                            id="middleName"
                            name="middleName"
                            label="Middle Name"
                            fullWidth
                            autoComplete="middleName"
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <FormControl component="fieldset" className={classes.formControl}>
                            <FormLabel component="legend">Gender</FormLabel>
                            <RadioGroup aria-label="gender" name="gender1" value={value} onChange={handleChange}>
                                <FormControlLabel value="female" control={<Radio color="primary"/>} label="Female" />
                                <FormControlLabel value="male" control={<Radio color="primary"/>} label="Male" />
                            </RadioGroup>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <CountrySelect onChange={onCountryChange} />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <ReactPhoneInput defaultCountry={'us'} onChange={handleOnChange} />
                    </Grid>
                    {/* <Grid item xs={12} >
                        <FormControlLabel
                            control={<Checkbox color="secondary" name="saveAddress" value="yes" />}
                            label="Use this address for payment details"
                        />
                    </Grid> */}
                </Grid>

                <Grid className="bd-container" container direction="row"
                    justify="flex-start" spacing={10}>
                    <Grid direction="row" justify="flex-start" container item xs={12} sm={2}>
                        <FormControl className={classes.formControl}>
                            <InputLabel id="demo-simple-select-label">Day</InputLabel>
                            <Select
                                labelId="Day"
                                id="Day"
                                value={day}
                                onChange={(event) => setDay(event.target.value)}
                            >
                                {Array.from(new Array(31), (v, i) =>
                                    <MenuItem value={i + 1}>{i + 1}</MenuItem>
                                )}
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid justify="flex-start" container item xs={12} sm={2}>
                        <FormControl className={classes.formControl}>
                            <InputLabel id="month">Month</InputLabel>
                            <Select
                                labelId="Month"
                                id="Month"
                                value={month}
                                onChange={(event) => setMonth(event.target.value)}
                            >
                                {Array.from(new Array(12), (v, i) =>
                                    <MenuItem value={i + 1}>{i + 1}</MenuItem>

                                )}
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid justify="flex-start" container item xs={12} sm={2}>
                        <FormControl className={classes.formControl}>
                            <InputLabel id="year">Year</InputLabel>
                            <Select
                                labelId="year"
                                id="year"
                                value={year}
                                onChange={(event) => setYear(event.target.value)}
                            >
                                {Array.from(new Array(90), (v, i) =>
                                    <MenuItem value={currentYear - i}>{currentYear - i}</MenuItem>

                                )}
                            </Select>
                        </FormControl>
                    </Grid>

                </Grid>

            </div>
        </React.Fragment>
    );
}
export default (ApllicantInfo);
