import React, { useState } from 'react';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import ReactPhoneInput from 'react-phone-input-2';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import CountrySelect from '../../../components/countrySelect'
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormLabel from '@material-ui/core/FormLabel';
import { TextField, withStyles, InputLabel, Select, MenuItem } from '@material-ui/core';
import "./spouse-basic.scss"
const useStyles = makeStyles(theme => ({
    formControl: {
        minWidth: 120,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
}));

function SpouseBasic({props,handleDataChange}) {
    const classes = useStyles();
    const [day, setDay] = useState(null);
    const [month, setMonth] = useState(null);
    const [year, setYear] = useState(null);
    const [maritalStatus, setMaritalStatus] = useState(null);
    const [education, setEducation] = useState(null);
    const [country, setCountry] = useState(null);
    const [value, setValue] = React.useState('female');
    const handleChange = event => {
        setValue(event.target.value);
    };
    function handleGenderChange(event) {
        handleDataChange("gender", event.target.value)
    }
    const onCountryChange = (value) => {
        setCountry(value);
        handleDataChange("country", value)
    }
    const currentYear = new Date().getFullYear() - 18;

    return (
        <React.Fragment >
            <div className="applicant-info-container">

                <div className="addressForm">
                    <Grid container spacing={5}>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                id="firstName"
                                name="firstName"
                                label="First name"
                                fullWidth
                                autoComplete="fname"
                                value={props && props.registrationStore.applicationData.spouseInfo.firstName}
                                onChange={(event) => handleDataChange("firstName", event.target.value)}

                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                id="lastName"
                                name="lastName"
                                label="Last name"
                                fullWidth
                                autoComplete="lname"
                                value={props && props.registrationStore.applicationData.spouseInfo.lastName}
                                onChange={(event) => handleDataChange("lastName", event.target.value)}

                            />
                        </Grid>
                        <Grid container justify="flex-start" item xs={12} sm={6}>
                            <TextField
                                required
                                id="middleName"
                                name="middleName"
                                label="Middle Name"
                                fullWidth
                                value={props && props.registrationStore.applicationData.spouseInfo.middleName}
                                onChange={(event) => handleDataChange("middleName", event.target.value)}

                            />
                            <FormControlLabel
                                control={<Checkbox color="primary" name="saveAddress" value="yes" />}
                                label="Don't have middle name"
                            />
                        </Grid>

                        <Grid item xs={12} sm={6}>
                            <CountrySelect placeHolder="Country Of Birth" onChange={onCountryChange} />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                id="cityOfBirth"
                                name="cityOfBirth"
                                label="City of Birth"
                                fullWidth
                                autoComplete="cityOfBirth"
                                value={props && props.registrationStore.applicationData.spouseInfo.cityOfBirth}
                                onChange={(event) => handleDataChange("cityOfBirth", event.target.value)}

                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <CountrySelect placeHolder="Country Of Residence" onChange={onCountryChange} />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                id="nationality"
                                name="nationality"
                                label="Nationality"
                                fullWidth
                                autoComplete="nationality"
                                value={props && props.registrationStore.applicationData.spouseInfo.nationality}
                                onChange={(event) => handleDataChange("nationality", event.target.value)}
                            />
                        </Grid>
                        <Grid container justify="flex-start" item xs={12} sm={6}>
                            <FormControl className={`select-input ${classes.formControl}`}>
                                <InputLabel id="demo-simple-select-label">Marital Status</InputLabel>
                                <Select
                                    labelId="maritalStatus"
                                    id="maritalStatus"
                                    value={maritalStatus}
                                    onChange={(event) => setMaritalStatus(event.target.value)}
                                    value={props && props.registrationStore.applicationData.spouseInfo.maritalStatus}
                                    onChange={(event) => handleDataChange("maritalStatus", event.target.value)}
                                >
                                    <MenuItem value="single">Single</MenuItem>
                                    <MenuItem value="married">Married</MenuItem>
                                    <MenuItem value="divorced">Divorced</MenuItem>
                                    <MenuItem value="Widowed">Widowed</MenuItem>

                                </Select>
                            </FormControl>
                            {props && props.registrationStore.applicationData.spouseInfo.maritalStatus === "married" && <FormControlLabel
                                control={<Checkbox color="primary" name="saveAddress" value="yes" />}
                                label="My spouse in a U.S. Citizen"
                            />}
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                id="childrenNumber"
                                name="childrenNumber"
                                label="Children Number"
                                type="number"
                                fullWidth
                                autoComplete="childrenNumber"
                                value={props && props.registrationStore.applicationData.spouseInfo.childrenNumber}
                                onChange={(event) => handleDataChange("childrenNumber", event.target.value)}

                            />
                        </Grid>
                        <Grid container justify="flex-start" item xs={12} sm={6}>
                            <FormControl className={`select-input ${classes.formControl}`}>
                                <InputLabel id="demo-simple-select-label">Education</InputLabel>
                                <Select
                                    labelId="education"
                                    id="education"
                                    value={props && props.registrationStore.applicationData.spouseInfo.education}
                                    onChange={(event) => handleDataChange("education", event.target.value)}
                                >
                                    <MenuItem value="highSchool">High School</MenuItem>
                                    <MenuItem value="diplomaIncomplete">Diploma Incomplete</MenuItem>
                                    <MenuItem value="highSchoolDiploma">High school Diploma</MenuItem>
                                    <MenuItem value="graduateLevelCourses">Graduate level courses</MenuItem>
                                    <MenuItem value="bachelorsDegree">Bachelors Degree</MenuItem>
                                    <MenuItem value="masterDegree">Master Degree</MenuItem>
                                    <MenuItem value="phd">Phd</MenuItem>

                                </Select>
                            </FormControl>
                        </Grid>
                        <Grid container justify="flex-start" item xs={12} sm={6}>
                            <FormControl component="fieldset" className={classes.formControl}>
                                <FormLabel component="legend">Gender</FormLabel>
                                <RadioGroup aria-label="gender" name="gender1" value={props && props.registrationStore.applicationData.spouseInfo.gender} onChange={handleGenderChange} row>
