import React, { Component,useState } from 'react';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import ReactPhoneInput from 'react-phone-input-2';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import CountrySelect from '../../components/countrySelect'
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormLabel from '@material-ui/core/FormLabel';
import { TextField, withStyles, InputLabel, Select, MenuItem } from '@material-ui/core';
import "./applicant-info.scss"
import { inject, observer } from "mobx-react";

const useStyles = makeStyles(theme => ({
    formControl: {
        minWidth: 120,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
}));
@inject('registrationStore')
@observer
class ApllicantInfo extends Component {
    // classes = useStyles();
    state = {
        maritalStatus: null,
        currentYear: null
    }
    handleGenderChange(event) {
        this.registrationStore.handleDataChange("gender", event.target.value)
    }
    onCountryChange = (value) => {
        this.setState({currentYear:value})
        this.registrationStore.handleDataChange("country", value)
    }
    currentYear = new Date().getFullYear() - 18;
    render(){
        const { classes } = this.props;

    return (
        <React.Fragment >
            <div className="applicant-info-container">

                <div className="addressForm">
                    <Grid container spacing={5}>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                id="firstName"
                                name="firstName"
                                label="First name"
                                fullWidth
                                autoComplete="fname"
                                value={this.registrationStore.applicationData.applicantInfo && this.registrationStore.applicationData.applicantInfo.firstName}
                                onChange={(event) => this.registrationStore.handleDataChange("firstName", event.target.value)}

                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                id="lastName"
                                name="lastName"
                                label="Last name"
                                fullWidth
                                autoComplete="lname"
                                value={this.registrationStore.applicationData.applicantInfo && this.registrationStore.applicationData.applicantInfo.lastName}
                                onChange={(event) => this.registrationStore.handleDataChange("lastName", event.target.value)}

                            />
                        </Grid>
                        <Grid container justify="flex-start" item xs={12} sm={6}>
                            <TextField
                                required
                                id="middleName"
                                name="middleName"
                                label="Middle Name"
                                fullWidth
                                value={this.registrationStore.applicationData.applicantInfo && this.registrationStore.applicationData.applicantInfo.middleName}
                                onChange={(event) => this.registrationStore.handleDataChange("middleName", event.target.value)}

                            />
                            <FormControlLabel
                                control={<Checkbox color="primary" name="saveAddress" value="yes" />}
                                label="Don't have middle name"
                            />
                        </Grid>

                        <Grid item xs={12} sm={6}>
                            <CountrySelect placeHolder="Country Of Birth" onChange={this.onCountryChange} />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                id="cityOfBirth"
                                name="cityOfBirth"
                                label="City of Birth"
                                fullWidth
                                autoComplete="cityOfBirth"
                                value={this.registrationStore.applicationData.applicantInfo && this.registrationStore.applicationData.applicantInfo.cityOfBirth}
                                onChange={(event) => this.registrationStore.handleDataChange("cityOfBirth", event.target.value)}

                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <CountrySelect placeHolder="Country Of Residence" onChange={this.onCountryChange} />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                id="nationality"
                                name="nationality"
                                label="Nationality"
                                fullWidth
                                autoComplete="nationality"
                                value={this.registrationStore.applicationData.applicantInfo && this.registrationStore.applicationData.applicantInfo.nationality}
                                onChange={(event) => this.registrationStore.handleDataChange("nationality", event.target.value)}
                            />
                        </Grid>
                        <Grid container justify="flex-start" item xs={12} sm={6}>
                            <FormControl className={`select-input ${this.classes.formControl}`}>
                                <InputLabel id="demo-simple-select-label">Marital Status</InputLabel>
                                <Select
                                    labelId="maritalStatus"
                                    id="maritalStatus"

                                    value={this.registrationStore.applicationData.applicantInfo && this.registrationStore.applicationData.applicantInfo.maritalStatus}
                                    onChange={(event) => this.registrationStore.handleDataChange("maritalStatus", event.target.value)}
                                >
                                    <MenuItem value="single">Single</MenuItem>
                                    <MenuItem value="married">Married</MenuItem>
                                    <MenuItem value="divorced">Divorced</MenuItem>
                                    <MenuItem value="Widowed">Widowed</MenuItem>

                                </Select>
                            </FormControl>
                            {this.registrationStore.applicationData.applicantInfo && this.registrationStore.applicationData.applicantInfo.maritalStatus === "married" && <FormControlLabel
                                control={<Checkbox color="primary" name="saveAddress" value="yes" />}
                                label="My spouse in a U.S. Citizen"
                            />}
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                required
                                id="childrenNumber"
                                name="childrenNumber"
                                label="Children Number"
                                type="number"
                                fullWidth
                                autoComplete="childrenNumber"
                                value={this.registrationStore.applicationData.applicantInfo && this.registrationStore.applicationData.applicantInfo.childrenNumber}
                                onChange={(event) => this.registrationStore.handleDataChange("childrenNumber", event.target.value)}

                            />
                        </Grid>
                        <Grid container justify="flex-start" item xs={12} sm={6}>
                            <FormControl className={`select-input ${this.classes.formControl}`}>
                                <InputLabel id="demo-simple-select-label">Education</InputLabel>
                                <Select
                                    labelId="education"
                                    id="education"
                                    value={this.registrationStore.applicationData.applicantInfo && this.registrationStore.applicationData.applicantInfo.education}
                                    onChange={(event) => this.registrationStore.handleDataChange("education", event.target.value)}
                                >
                                    <MenuItem value="highSchool">High School</MenuItem>
                                    <MenuItem value="diplomaIncomplete">Diploma Incomplete</MenuItem>
