import { observable, action, computed} from "mobx";
import ConfigStore from "./configStore"

const apis = {
    login(data){
      return fetch(ConfigStore.configData.domain + 'api/users/login', {
            method: 'POST',
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              "user": {
                "email": "sari.q34@gmail.com",
                "password": "1298"
              }
            }),
          })
          .then((response) => response.json())
          .then((responseJson) => {
            if(responseJson.user){
              console.log(responseJson.user.userData)
              this.userData = responseJson.user.userData;
            }else{
            }        
            return responseJson;
          })
          .catch((error) => {
            console.error(error);
          });
    },
    singOut(data){
        fetch(ConfigStore.configData.domain + 'api/users/signOut/', {
            method: 'POST',
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
              'Authorization': 'Token ' + AuthStore.authData.user.token

            },
            body: JSON.stringify({
              "user": data
            }),
          })
          .then((response) => response.json())
          .then((responseJson) => {
            console.log(responseJson)
            AsyncStorage.removeItem('userData');

            this.props.navigation.navigate('Auth');
          })
          .catch((error) => {
            console.log(error);
          });
    }
}
class AuthStore {
    @observable authData=null;

    @action login () {
        apis.login().then((data)=>this.userData = data.user.userData);
    };
    @action getUserDataLocal () {
        this.authData = localStorage.getItem('authData');
    };
    @action setUserDataLocal (data) {
        localStorage.setItem('authData', JSON.stringify(data));
        this.authData = data;
        console.log(this.authData)
    };
    @action initLogin () {
        this.authData = JSON.parse(localStorage.getItem('authData'));
    };
    @action signOut(){
        apis.singOut(this.authData);
    }
}
export default new AuthStore();
