import { observable, action, computed} from "mobx";
import ConfigStore from "./configStore"

const apis = {
    login(data){
      return fetch(ConfigStore.configData.domain + 'api/users/login', {
            method: 'POST',
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              "user": {
                "email": "sari.q34@gmail.com",
                "password": "1298"
              }
            }),
          })
          .then((response) => response.json())
          .then((responseJson) => {
            if(responseJson.user){
              console.log(responseJson.user.userData)
              this.userData = responseJson.user.userData;
            }else{
            }        
            return responseJson;
          })
          .catch((error) => {
            console.error(error);
          });
    }
}
class AuthStore {
    @observable userData=null;

    @action login () {
        apis.login().then((data)=>this.userData = data.userData);
    };
}
export default new AuthStore();
